import React, { Component } from 'react';
import Header from './components/layout/Header'
import Comments from './components/Comments'
import AddComment from './components/AddComment'
import './App.css';

class App extends Component {
	state = {
		comments:[
			{
				id: 1,
				timestamp: 'created on...',
				text: 'Bad snippet',
				startLine: 0,
				startCharIndex: 0,
				endLine: 0,
				endCharIndex: 0,
				delete: false
			},
						{
				id: 2,
				timestamp: 'created on...',
				text: 'Not bad snippet',
				startLine: 0,
				startCharIndex: 0,
				endLine: 0,
				endCharIndex: 0,
				delete: false
			},
						{
				id: 3,
				timestamp: 'created on...',
				text: 'Good snippet',
				startLine: 0,
				startCharIndex: 0,
				endLine: 0,
				endCharIndex: 0,
				delete: false
			}
		]
	}

	// toggle delete comment
	markDelete = (id) => {
		this.setState({ comments: this.state.comments.map(comment => {  
			if(comment.id === id) {
				comment.delete = !comment.delete
			}
			return comment;
		}) });
	}

	// delete comment
	delComment = (id) => {
		this.setState({ comments: [...this.state.comments.filter(comment => comment.id !== id)] });
	}


	render() {
		return (
			<div className="App">
				<Header />
				<Comments comments={this.state.comments} markDelete=
				{ this.markDelete } delComment={ this.delComment }/>
				<AddComment />
			</div>
		);
	}
}

export default App;
