import React, { Component } from 'react';

export class AddComment extends Component {

	state = {
		text: ''		
	}

	render() {
		return (
			<form style={{ display: 'flex' }}>
				<input
					type="text"
					name="text"
					style={{ flex: '10', padding: '5px' }}
					placeholder="Add Comment..."
					value={this.state.text}
				/>
				<input
					type="submit"
					value="Add"
					className="btn"
					style={{ flex: '1' }}
				/>
			</form>
		)
	}
}

export default AddComment;
