import React, { Component } from 'react';
import Comments from './components/Comments'
import './App.css';

class App extends Component {
	state = {
		comments:[
			{
				id: 1,
				timestamp: 'created on...',
				text: 'Bad snippet',
				startLine: 0,
				startCharIndex: 0,
				endLine: 0,
				endCharIndex: 0,
				delete: false
			},
						{
				id: 2,
				timestamp: 'created on...',
				text: 'Not bad snippet',
				startLine: 0,
				startCharIndex: 0,
				endLine: 0,
				endCharIndex: 0,
				delete: true
			},
						{
				id: 3,
				timestamp: 'created on...',
				text: 'Good snippet',
				startLine: 0,
				startCharIndex: 0,
				endLine: 0,
				endCharIndex: 0,
				delete: false
			}
		]
	}


	render() {
		return (
			<div className="App">
				<Comments comments={this.state.comments}  />
			</div>
		);
	}
}

export default App;
