import React, { Component } from 'react';
import Header from './components/layout/Header'
import Comments from './components/Comments'
import AddComment from './components/AddComment'
import './App.css';

// uuidv4 generates random ids 
const { v4: uuidv4 } = require('uuid');	

class App extends Component {
	state = {
		comments:[
			{
				id: uuidv4(),						// generate a random unique id
				timestamp: Date.now(),
				text: 'Bad snippet',
				startLine: 0,
				startCharIndex: 0,
				endLine: 0,
				endCharIndex: 0,
				delete: false
			},
			{
				id: uuidv4(),
				timestamp: 'created on...',
				text: 'Not bad snippet',
				startLine: 0,
				startCharIndex: 0,
				endLine: 0,
				endCharIndex: 0,
				delete: false
			},
			{
				id: uuidv4(),
				timestamp: 'created on...',
				text: 'Good snippet',
				startLine: 0,
				startCharIndex: 0,
				endLine: 0,
				endCharIndex: 0,
				delete: false
			}
		]
	}

	// toggle delete comment
	markDelete = (id) => {
		this.setState({ comments: this.state.comments.map(comment => {  
			if(comment.id === id) {
				comment.delete = !comment.delete
			}
			return comment;
		}) });
	}

	// delete comment
	delComment = (id) => {
		this.setState({ comments: [...this.state.comments.filter(comment => comment.id !== id)] });
	}

	// add comment
	addComment = (text) => {
		const newComment = {
				id: uuidv4(),
				timestamp: 'created on...',
				text,
				startLine: 0,
				startCharIndex: 0,
				endLine: 0,
				endCharIndex: 0,
				delete: false
		}
		this.setState({ comments: [...this.state.comments, newComment] });
	} 

	render() {
		return (
			<div className="App">
				<div className="Container">
					<Header />
					<Comments comments={this.state.comments} markDelete=
					{ this.markDelete } delComment={ this.delComment }/>
					<AddComment addComment={ this.addComment } />
				</div>
			</div>
		);
	}
}

export default App;
